mapper.update:

	UPDATE board SET  
	readcount = readcount + 1 
	WHERE BOARDNUM = #{boardNum}

mapper.insert:

   select 
      seq_board.nextval boardid 
   from dual

INSERT INTO board (
     pid
   , boardid
   , title
   , contents
   , writer_uid
   , reg_date
) VALUES (
      #{pid} 
   , #{boardid}
   , #{title}
   , #{contents}
   , #{writer_uid}
   , sysdate
)

mapper.update:
update board set 
     title=#{title}
   , content=#{content}
where BOARDNUM=#{boardNum}

mapper.delete:
delete from board 
where BOARDNUM in
 
   #{boardnum}

delete from board 
where PNUM in
 
   #{boardnum}

mapper.select:
select id 
from board
where BOARDNUM=#{pnum}
